        private void ResetProperties()
        {
            // Reset possibly-changed properties to default, since WPF's virtualization recycles the same controls without restoring their default values
            rootGrid.Background = new SolidColorBrush(Color.FromArgb(0, 0, 0, 0));
            viewUserMi.IsEnabled = false;
            openDMMi.IsEnabled = false;
            copyUserIdMi.IsEnabled = false;
            copyGuildIdMi.IsEnabled = false;
            deleteMessageMi.IsEnabled = true;
        }

        private void UpdateMetadata()
        {
            var channel = this.Message.channel;
            string metadata = "Channel: ";
            if (channel.name != null)
            {
                if (channel.IsDM() || channel.IsGroupDM() || channel.IsVoice())
                {
                    metadata += channel.name;
                }
                else
                {
                    metadata += $"#{channel.name}";
                }
            }
            else if (channel.IsDM() && this.User != null)
            {
                copyUserIdMi.IsEnabled = true;

                var recipientId = this.Message.channel.GetOtherDMRecipient(this.User);
                var recipientUserRelationship = Array.Find(this.User.relationships, x => x.id == recipientId);
                if (recipientUserRelationship != null)
                {
                    metadata += $"DMs with {recipientUserRelationship.user.GetTag()}";
                }
                else
                {
                    metadata += $"DMs with {recipientId}";
                }
            }
            else
            {
                metadata += $"unknown ({channel.id}, {(channel.IsGroupDM() ? "Group DM" : channel.IsDM() ? "DM" : "Text Channel")})";
            }

            if (channel.guild != null)
            {
                copyGuildIdMi.IsEnabled = true;
                viewInGuildsTab.IsEnabled = true;

                metadata += ", Guild: ";
                if (channel.guild.name != null)
                {
                    metadata += channel.guild.name;
                }
                else
                {
                    metadata += $"unknown ({channel.guild.id})";
                }
            }

            if (this.Message.attachments.Count > 0)
            {
                metadata += $", {this.Message.attachments.Count} attachments";
            }

            metadataLb.Content = metadata;
        }

        public void LoadMessageData(DMessage message)
        {
            ResetProperties();

            if (message.channel.IsDM())
            {
                viewUserMi.IsEnabled = true;
                openDMMi.IsEnabled = true;
            }

            // Set metadata
            UpdateMetadata();

            // Set content and timestamp
            ParseAndSet(message.content);
            dateLb.Content = message.timestamp.ToShortDateString() + " " + message.timestamp.ToShortTimeString();

            // Add red tint if the message was deleted with mass deleter
            if (message.deleted)
            {
                MarkDeleted();
            }
        }

        public void LoadUserData(DUser user)
        {
            // Set username
            usernameLb.Content = user.global_name ?? user.GetTag();

            // Update metadata
            if(this.Message != null && this.Message.channel.IsDM())
            {
                UpdateMetadata();
            }

            // Set avatar
            avatarImg.ImageSource = user.avatar_image;
        }

        public void MarkDeleted()
        {
            rootGrid.Background = new SolidColorBrush(Color.FromArgb(255, 78, 54, 59));
            deleteMessageMi.IsEnabled = false;
        }

        private void ParseAndSet(string content)
        {
            contentLb.Text = "";

            bool isOnlyEmojis = UEmojiRegex.Replace(DEmojiRegex.Replace(content, ""), "").Trim() == "";

            var lines = content.Split('\n');
            for(int i=0;i<lines.Length;i++)
            {
                if(i > 0)
                {
                    contentLb.Inlines.Add(new Run("\n"));
                }

                var line = lines[i];

                string current = "";
                while(line.Length > 0)
                {
                    char c = line[0];
                    if (c == '<') // Render custom emojis
                    {
                        var match = DEmojiStartRegex.Match(line);
                        if (match.Success)
                        {
                            // Push the current text
                            contentLb.Inlines.Add(new Run(current));
                            current = "";

                            var isAnimated = match.Groups[1].Value == "a";
                            var emojiId = match.Groups[2].Value;

                            var img = new Image();
                            if (isOnlyEmojis)
                            {
                                img.Width = 48;
                                img.Height = 48;
                            }
                            else
                            {
                                img.Width = 22;
                                img.Height = 22;
                            }

                            BitmapImage bitmap = new BitmapImage();
                            bitmap.BeginInit();
                            bitmap.UriSource = new Uri($"https://cdn.discordapp.com/emojis/{emojiId}.{(isAnimated ? "gif" : "png")}?size={(isOnlyEmojis ? "96" : "44")}&quality=lossless", UriKind.Absolute);
                            bitmap.UriCachePolicy = new RequestCachePolicy(RequestCacheLevel.CacheIfAvailable);
                            bitmap.EndInit();
                            img.Source = bitmap;

                            var inlineContainer = new InlineUIContainer(img)
                            {
                                BaselineAlignment = BaselineAlignment.Center
                            };
                            contentLb.Inlines.Add(inlineContainer);

                            line = DEmojiStartRegex.Replace(line, ""); // The regex uses ^ so it will only replace the current emoji
                            continue;
                        }
                    } else if(c == 'h') {
                        var match = URLRegex.Match(line);
                        if(match.Success)
                        {
                            contentLb.Inlines.Add(new Run(current));
                            current = "";

                            string url = match.Groups[1].Value;
                            var hyperlink = new Hyperlink(new Run(url))
                            {
                                Foreground = new SolidColorBrush(Color.FromArgb(255, 0, 168, 252)),
                                NavigateUri = new Uri(url),
                                TextDecorations = null
                            };
                            hyperlink.RequestNavigate += (o, e) =>
                            {
                                Process.Start(new ProcessStartInfo
                                {
                                    FileName = e.Uri.ToString(),
                                    UseShellExecute = true
                                });
                            };
                            hyperlink.MouseEnter += (o, e) => hyperlink.TextDecorations = TextDecorations.Underline; // TODO: figure out how to do this properly with Resources and Style
                            hyperlink.MouseLeave += (o, e) => hyperlink.TextDecorations = null;
                            contentLb.Inlines.Add(hyperlink);

                            line = URLRegex.Replace(line, "");
                            continue;
                        }
                    } else // Turn unicode emojis into twemoji
                    {
                        // FIXME: face_holding_back_tears isn't detected by the regex
                        var match = UEmojiStartRegex.Match(line);
                        if(match.Success)
                        {
                            contentLb.Inlines.Add(new Run(current));
                            current = "";

                            string emoji = match.Groups[1].Value;

                            // Strip off variation selector, used by heart emoji for example
                            // https://github.com/twitter/twemoji/blob/d94f4cf793e6d5ca592aa00f58a88f6a4229ad43/scripts/build.js#L347
                            if (!emoji.Contains("\u200D")) {
                                Regex.Replace(emoji, @"\uFE0F", "");
                            }

                            var codepoints = new List<int>();
                            for(int j=0;j<emoji.Length;j++)
                            {
                                if(emoji.Length >= j + 2 && char.IsSurrogatePair(emoji[j], emoji[j + 1]))
                                {
                                    codepoints.Add(char.ConvertToUtf32($"{emoji[j]}{emoji[j + 1]}", 0));
                                    j++;
                                } else
                                {
                                    codepoints.Add(emoji[j]);
                                }
                            }

                            string codepoint = String.Join("-", codepoints.Select(x => x.ToString("X").ToLower()).ToArray());
                            var img = new Image();
                            if (isOnlyEmojis)
                            {
                                img.Width = 48;
                                img.Height = 48;
                            }
                            else
                            {
                                img.Width = 22;
                                img.Height = 22;
                            }

                            BitmapImage bitmap = new BitmapImage();
                            bitmap.BeginInit();
                            bitmap.UriSource = new Uri($"https://twemoji.maxcdn.com/v/14.0.2/72x72/{codepoint}.png", UriKind.Absolute);
                            bitmap.UriCachePolicy = new RequestCachePolicy(RequestCacheLevel.CacheIfAvailable);
                            bitmap.EndInit();
                            img.Source = bitmap;

                            var inlineContainer = new InlineUIContainer(img)
                            {
                                BaselineAlignment = BaselineAlignment.Center
                            };
                            contentLb.Inlines.Add(inlineContainer);

                            line = UEmojiStartRegex.Replace(line, "");
                            continue;
                        }
                    }

                    current += c;
                    line = line.Substring(1);
                }

                contentLb.Inlines.Add(new Run(current));
            }

            // Attachments
            bool firstAttachment = true;
            foreach(var attachment in Message.attachments)
            {
                if(content != "" || !firstAttachment)
                {
                    contentLb.Inlines.Add(new Run("\n"));
                }
                firstAttachment = false;

                if(attachment.IsImage())
                {
                    var img = new Image();
                    img.MaxWidth = 400;
                    img.MaxHeight = 300;
                    img.Stretch = Stretch.Uniform;
                    img.StretchDirection = StretchDirection.DownOnly;
                    img.Cursor = Cursors.Hand;
                    img.MouseUp += (s, e) =>
                    {
                        if (e.ChangedButton == MouseButton.Left)
                        {
                            Process.Start(new ProcessStartInfo
                            {
                                FileName = attachment.url,
                                UseShellExecute = true
                            });
                        }
                    };

                    if (Discord.AttachmentsCache.ContainsKey(attachment.id))
                    {
                        img.Source = Discord.AttachmentsCache[attachment.id];
                    }
                    else
                    {
                        ImageBehavior.SetAnimatedSource(img, Discord.LoadingAnim);
                        ThreadPool.QueueUserWorkItem(async state =>
                        {
                            try
                            {
                                using (var httpClient = new HttpClient())
                                using (var response = await httpClient.GetAsync(attachment.url))
                                {
                                    if (response.IsSuccessStatusCode)
                                    {
                                        using (var stream = new MemoryStream())
                                        {
                                            await response.Content.CopyToAsync(stream);
                                            stream.Seek(0, SeekOrigin.Begin);

                                            BitmapImage bitmap = new BitmapImage();
                                            bitmap.BeginInit();
                                            bitmap.DecodePixelHeight = 300;
                                            bitmap.StreamSource = stream;
                                            bitmap.CacheOption = BitmapCacheOption.OnLoad;
                                            bitmap.EndInit();
                                            bitmap.Freeze();

                                            await Dispatcher.BeginInvoke(new Action(() =>
                                            {
                                                Discord.AttachmentsCache[attachment.id] = bitmap;

                                                ImageBehavior.SetAnimatedSource(img, null);
                                                img.Source = bitmap;
                                            }));
                                        }
                                    }
                                }
                            }
                            catch (Exception) { }
                        });
                    }

                    var inlineContainer = new InlineUIContainer(img)
                    {
                        BaselineAlignment = BaselineAlignment.Center,
                    };
                    contentLb.Inlines.Add(inlineContainer);
                } else
                {
                    var file = new FileAttachmentWPF
                    {
                        FileName = attachment.filename,
                        Url = attachment.url
                    };

                    var inlineContainer = new InlineUIContainer(file)
                    {
                        BaselineAlignment = BaselineAlignment.Center
                    };
                    contentLb.Inlines.Add(inlineContainer);
                }
            }
        }

        private void goToMessageMi_Click(object sender, RoutedEventArgs e)
        {
            if(this.Message.channel.has_duplicates)
            {
                Util.MsgBoxWarn(Consts.DuplicateDMWarning);
            }

            try
            {
                var link = this.Message.GetMessageLink();
                Discord.LaunchDiscordProtocol($"channels/{link}");
            }
            catch (Exception ex)
            {
                Util.MsgBoxErr(ex.Message);
            }
        }

        private void Grid_MouseEnter(object sender, MouseEventArgs e)
        {
            if(this.Message.deleted)
            {
                rootGrid.Background = new SolidColorBrush(Color.FromArgb(255, 73, 51, 55));
            } else
            {
                rootGrid.Background = new SolidColorBrush(Color.FromArgb(255, 46, 48, 53));
            }
        }

        private void Grid_MouseLeave(object sender, MouseEventArgs e)
        {
            if(this.Message.deleted)
            {
                rootGrid.Background = new SolidColorBrush(Color.FromArgb(255, 78, 54, 59));
            } else
            {
                rootGrid.Background = new SolidColorBrush(Color.FromArgb(0, 0, 0, 0));
            }
        }

